rule "remove unused start action fields"
when
  ( has_field("action") &&
to_string($message."action") == "start"
)

then
  remove_field(
    field : "duration",
    invert : false
  );
  remove_field(
    field : "rcvdbyte",
    invert : false
  );
  remove_field(
    field : "rcvdpkt",
    invert : false
  );
  remove_field(
    field : "sentbyte",
    invert : false
  );
  remove_field(
    field : "sentpkt",
    invert : false
  );
end

